
public class DemoIncrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int v = 4;
		int plusPlusv = ++v;
		
		v = 4;
		int vPlusPlus = v++;
		
		System.out.println("v is " + v);
		System.out.println("++v is " + plusPlusv);
		System.out.println("v++ is " + vPlusPlus);
	}

}
